#!/usr/bin/env python3 # -*- coding: utf-8 -*-"""Created on Mon Jul 27 13:28:18 2020@author: yuwen"""#This file is to combine different years and divide them into subset level (10 sentences) #TRAIN#year 16-18: de, ru - en, en - de#year 17-18: fi, zh - en, en - ru, fi#DEV-TEST#year 19: de, fi, ru, zh - en; en - de, ru, fi#TRAIN-DEV-TEST #year 19: lt, kk, gu - en, en - ltimport syssys.path.append("../")from util.utils import get_orig, make_training_and_labels, generate_file_name, subset_sentencesimport pandas as pdyear = ['2017','2018']lang_pair = ['enfi']for i in lang_pair:    df = pd.DataFrame(columns = ['TRAIN', 'LABEL', 'SRC'])    for j in year:         print('Now we are processing', j, i)        mt_file, ht_file, src_file = generate_file_name(j, i)        print('MT filename is: ', mt_file)        print('HT filename is: ', ht_file)        print('SRC filename is: ', src_file)                mt, ht, src = get_orig(mt_file, ht_file, src_file, i[:2],document_level=False)                #turn into a subset of 10 sentences                 mt_sub = subset_sentences(mt,num=10)        ht_sub = subset_sentences(ht,num=10)        src_sub = subset_sentences(src,num=10)                print('mt # at subset level:', len(mt_sub))        print('ht # at subset level:', len(ht_sub))        print('src # at subset level:', len(src_sub))                      training_data, true_label = make_training_and_labels(mt_sub, ht_sub)        new_df = pd.DataFrame(data={"TRAIN": training_data, "LABEL": true_label, "SRC":src_sub*2})                #append different years to the dataframe        df = df.append(new_df,ignore_index=True)            df.to_pickle('/Users/chenfish/Desktop/Thesis/Project/data/mt_ht/wmt/5/2019only/'+ str(i) + '.train')